INSERT INTO `products` (`id`, `name`, `cate_id`, `image`, `price`, `sale`, `quantity`, `intro`, `detail`, `view`) VALUES
(24, 'MAINBOARD ASUS ROG STRIX X570-E GAMING (AMD X570, SOCKET AM4, ATX, 4 KHE RAM', 2, '250_47571_rog_strix_x570_e_gaming_aura_sync_01.png', 4900000, 0, 20, 'S5200 là máy ảnh có thiết kế thời trang, cho ảnh chụp chất lượng. Trang bị cảm biến BSI CMOS 16MP, ống kính Nikkor zoom quang học 6x, tiêu cự từ 26mm đến 156mm (quy đổi định dạng 35mm).\r\nHơn nữa, máy còn được trang bị nhiều chức năng mới như hiệu ứng Glamour Retouch và tuỳ chọn “continuous” cho chức năng Smile timer...', 'Máy ảnh NIKON COOLPIX S5200\r\nS5200 là máy ảnh có thiết kế thời trang, cho ảnh chụp chất lượng. Trang bị cảm biến BSI CMOS 16MP, ống kính Nikkor zoom quang học 6x, tiêu cự từ 26mm đến 156mm (quy đổi định dạng 35mm).\r\nHơn nữa, máy còn được trang bị nhiều chức năng mới như hiệu ứng Glamour Retouch và tuỳ chọn “continuous” cho chức năng Smile timer...\r\nNhững tính năng chính của COOLPIX S5200\r\nỐng kính Nikkor zoom quang học 6x, tiêu cự từ 25mm đến 156mm (quy đổi định dạng 35mm)\r\n \r\nỐng kính Nikkor zoom 6x được trang bị trên 1 thân máy nhỏ gọn, có thể mang đi khắp nơi.\r\nCảm biến BSI CMOS 16MP cho ảnh chụp đẹp trong điều kiện thiếu sáng như chụp trong nhà và chụp phong cảnh đêm\r\nS5200 được  trang bị cảm biến BSI CMOS 16MP cho ảnh chụp đẹp, ít nhiễu khi chụp trong điều kiện ánh sáng yếu. Những lợi ích của cảm biến rõ ràng nhất với bức ảnh chụp được thực hiện trong các hoàn cảnhánh sáng lờ mờ, chẳng hạn như trong nhà hoặc khi chụp phong cảnh đêm.\r\n \r\n \r\nKết nối Wifi dễ dàng\r\nVới việc tích hợp Wi-Fi, S5200 có thể dễ dàng chia sẽ dữ liệu với các thiết bị thông minh. Điều này cho phép người dùng có thể chuyển ảnh chất lượng cao và phim quay được từ S5200 đến các thiết bị thông minh, từ đó có thể tải lên mạng xã hội một cách dễ dàng. Hơn nữa, khi kết nối với các thiết bị thông minh, người dùng có thể dùng các thiết bị này để điều khiển máy ảnh và có thể tự chụp chân dung của chính mình rất dễ dàng.  \r\n \r\nNắm bắt những điểm tham quan và âm thanh\r\nQuay phim HD 1080p với âm thanh mang lại kỷ niệm của bạn với cuộc sống.', 17),
(26, 'MAINBOARD ASUS ROG X570 CROSSHAIR VIII HERO (AMD X570, SOCKET AM4, ATX, 4 KHE RAM', 2, '250_47570_rog_crosshair_viii_hero_aura_sync_01.png', 10709000, 0, 30, 'S5200 là máy ảnh có thiết kế thời trang, cho ảnh chụp chất lượng. Trang bị cảm biến BSI CMOS 16MP, ống kính Nikkor zoom quang học 6x, tiêu cự từ 26mm đến 156mm (quy đổi định dạng 35mm).', 'Máy ảnh NIKON COOLPIX S5200\r\nS5200 là máy ảnh có thiết kế thời trang, cho ảnh chụp chất lượng. Trang bị cảm biến BSI CMOS 16MP, ống kính Nikkor zoom quang học 6x, tiêu cự từ 26mm đến 156mm (quy đổi định dạng 35mm).\r\nHơn nữa, máy còn được trang bị nhiều chức năng mới như hiệu ứng Glamour Retouch và tuỳ chọn “continuous” cho chức năng Smile timer...\r\nNhững tính năng chính của COOLPIX S5200\r\nỐng kính Nikkor zoom quang học 6x, tiêu cự từ 25mm đến 156mm (quy đổi định dạng 35mm)\r\n \r\nỐng kính Nikkor zoom 6x được trang bị trên 1 thân máy nhỏ gọn, có thể mang đi khắp nơi.\r\nCảm biến BSI CMOS 16MP cho ảnh chụp đẹp trong điều kiện thiếu sáng như chụp trong nhà và chụp phong cảnh đêm\r\nS5200 được  trang bị cảm biến BSI CMOS 16MP cho ảnh chụp đẹp, ít nhiễu khi chụp trong điều kiện ánh sáng yếu. Những lợi ích của cảm biến rõ ràng nhất với bức ảnh chụp được thực hiện trong các hoàn cảnhánh sáng lờ mờ, chẳng hạn như trong nhà hoặc khi chụp phong cảnh đêm.', 4),
(27, 'NGUỒN MÁY TÍNH MSI MAG A550BN 550W ( 80 PLUS BRONZE/MÀU ĐEN)', 23, '250_61260_psu_mag_a550bn__1_.jpg', 1289000, 0, 2, 'Sau một thời gian ra mắt phiên bản NX20 ở phân khúc mirrorless cao cấp, Samsung vừa giới thiệu trước thềm CES 2014 mẫu NX30 được nâng cấp với nhiều cải tiến đáng giá. Không còn phát triển máy ảnh DSLR, Samsung tập trung vào mirrorless với dòng NX hai số, có thiết kế hầm hố, phím điều khiển đa dạng và nhiều tính năng cao cấp. NX30 mang những đường nét quen thuộc của mẫu Galaxy NX nhưng sở hữu tính năng mới gần đây cuả hãng như cảm biến ảnh CMOS 20.3 MP kích thước APS-C, hệ thống lấy nét lai, màn trập 1/8000, dải ISO tối đa 25.600, tích hợp Wi-Fi và NFC. Cùng với sản phẩm này, Samsung cũng giới thiệu dòng ống kính cao cấp Premium S series 16-50mm f/2-2.8 phù hợp với thiết kế thân máy và khả năng chụp ảnh trong nhiều điều kiện khác nhau.', '<h2 class=\"ddnb-title spct-title\" style=\"font-weight: 600; font-size: 22px; margin-bottom: 10px; padding-bottom: 10px; border-bottom: 1px solid rgb(234, 237, 241); color: rgb(34, 34, 34); font-family: -apple-system, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, sans-serif, &quot;Apple Color Emoji&quot;, &quot;Segoe UI Emoji&quot;, &quot;Segoe UI Symbol&quot;;\">Đánh giá FSP Power Supply DAGGER PRO Series Model SDA2-650 - Active PFC 80Plus Gold| chính hãng tại HanoiComputer</h2><h3 align=\"center\" style=\"font-weight: 700; color: rgb(34, 34, 34); font-family: -apple-system, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, sans-serif, &quot;Apple Color Emoji&quot;, &quot;Segoe UI Emoji&quot;, &quot;Segoe UI Symbol&quot;;\">FSP Power Supply DAGGER PRO Series Model SDA2-650 -&nbsp;Active PFC (80 Plus Gold/Full Modular/Màu Đen/SFX)&nbsp;</h3><p style=\"color: rgb(34, 34, 34); font-family: -apple-system, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, sans-serif, &quot;Apple Color Emoji&quot;, &quot;Se